exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`http://m.ixinmo.com/shu/${bid}.html`,html=>({name:html.subRegexLast(/<li>\s*<a [^>]*href="[^"]+">([^<]+)</g),updateTime:html.subRegex(/class="zz">更新：([^<]+)</)}),{clientOptions:{...(options&&options.clientOptions)}})};