exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`http://www.ting299.com/yousheng/${bid}.html`,html=>{const updateTime=html.subRegex(/<div>更新时间：<span>(?<updateTime>[^<]+)/),name=html.subRegexLast(/<li[^>]*><a[^>]*href="[^"]+\d+\.html\s*"[^>]*>(?<name>[^<]+)</g);return {name,updateTime}},{clientOptions:{...(options&&options.clientOptions),"x-mobile":0}})};