exports=async function(bid,options){const html=await getHtml(`http://m.ychy.cc/play/${bid}-0-1.html`,{...options?.clientOptions,"x-mobile":1,encoding:"gbk"});return formatCatalog(JSON.parse(html.replace(/'/g,"\"").subRegex(/var VideoListJson=(\[\[[^\]]+\]\]\])/))[0][1].map((_,i)=>{const[name,url]=_.split("$");return {id:i.toString(),name,url}}))};