exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`http://m.ychy.cc/book/${bid}.html`,html=>({updateTime:html.subRegex(/更新时间：<span>(?<updateTime>[^<]+)</),name:html.subIndexOf("id=\"playlist\"").toIndexOf("</ul>").subLastIndexOf("<a ").subRegex(/>([^<]+)<\/a>/)}),{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"}})};