exports=async function(bid,options){const{parseCatalogPaginate,cleanCatalogWrapper,hrefRecordRegex,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`https://www.ysts.cc/tingshu/${bid}/${1===page?"":"p"+page+".html"}`,{cleaner:html=>cleanCatalogWrapper(html,/id="playlist"/,"</ul>"),recordRegex:hrefRecordRegex},{...options,clientOptions,pageSize:30,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u96C6"))?.subRegex(/p(\d+)\.html/)?.intValue()})};