exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`https://m.zgpingshu.com/pingshu/${bid}/`,html=>{let fragment=html.substring(html.lastIndexOf("weui_cell_primary"));fragment=fragment.substring(0,fragment.indexOf("</div>"));const name=fragment.subRegex(/<\/i>([^<]+)</).trim();return {name,updateTime:null}},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk","x-mobile":1}})};