exports=async function(url,bid,cid,options){const{parseContentPaginate,hasMorePageCleaner,getLinkElement}=await require("transcode/parse_content"),replaceFont=await require("transcode/font_map");return parseContentPaginate(page=>`https://www.101du.net/html/${bid}/${cid}${1===page?"":"_"+page}.html`,"#chaptercontent",{clientOptions:{...(options&&options.clientOptions)},preprocess:html=>replaceFont(html,/<i>&#x([a-zA-Z\d]{4});?<\/i>/g),hasNextFn:html=>null!=/\/\d+_\d+\.html/.exec(getLinkElement(html,"id=\"next_url\"")),pageCleaner:hasMorePageCleaner,transformer:f=>f.replace(/<p>[^<]+提示您：看后求收藏.+接着再看更方便。[^<]*<\/p>/,"")})};