exports=async function(url,bid,cid,options){const{parseContent,trimBeforeContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},preprocess:html=>html.replace(/id="content"[^>]*>[^<]+最快更新<a[^>]*>[^<]+<\/a>最新章节！/,"id=\"content\">")})};