exports=async function(url,bid,cid,options){const{parseContent,removeTrailingObfuscateTransformer}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions)},transformer:removeTrailingObfuscateTransformer,preprocess:html=>html.replace(/】\n+【/g,"")})};