exports=async function(bid,options){const{parseCatalogPaginate,cleanDescendingNewest}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`https://www.17zw.com/book/${bid}/${1===page?"":"index_"+page+".html"}`,"div#list>dl>dd",{...options,clientOptions,pageSize:60,totalPagesRegex:/<option value="\/book\/\d+\/index_(?<totalPages>\d+)\.html">[^<]+<\/option>\s*<\/select>/}).then(_=>(_.catalog=cleanDescendingNewest(_.catalog),_))};