exports=async function(url,bid,cid,options){const{parseContentPaginate,getLinkElement,hasMorePageCleaner}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:`${url.replace(/\/$/,"")}_${page}`,"div#content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/xs_\d+_\d+_\d+/.exec(getLinkElement(html,"id=\"next_url\"")),preprocess:h=>h.replace(/<p>\s*点击读下一页(?:[^<]|<\/?[ab][^<]*>)+<\/p>/,""),pageCleaner:hasMorePageCleaner})};