exports=async function(url,bid,cid,options){const{parseContentPaginate}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"#TextContent",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},hasNextFn:html=>/url_next:['"]\/book\/\d+\/\d+_\d+\.html/.test(html),transformer:fragment=>fragment.replace(/<p>铅笔小说 \w+\.\w+<\/p>/,"")})};