exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/id="book_text"[^>]*>(?:\s*<div id="chapterContent">)?/)},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},preprocess:html=>html.replace(/天才一秒记住(?:[^<]|<br\s*\/?>)+<a [^>]+>[^<]*<\/a>最快更新！无广告！/,"")})};