exports=async function(url,bid,cid,options){const{parseContentPaginate,trimContentFragment}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),{cleaner:html=>trimContentFragment(html,/class="txtnav"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/read\/\d+\/\d+_\d+\.html"[^>]*>\s*下一/.exec(html),transformer:f=>f.replace(/^\s*[^<]+/,"")})};