exports=async function(bid,options){const{parseCatalogPaginate}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),"x-mobile":1},url=`http://www.30wx.net/chapters/${bid}`,URL=require("url"),uri=new URL(url);return parseCatalogPaginate(page=>url+`/${page}`,null,{...options,httpOptions:{method:"POST",headers:{"X-Requested-With":"XMLHttpRequest",Referer:url}},clientOptions,pageSize:20,totalPagesRegex:/"last_page"\s*:\s*(?<totalPages>\d+)/,parser:html=>JSON.parse(html).chapters.map(_=>({id:_.bid+"",name:_.name.trim(),url:uri.resolve(`/shu${bid}/${_.bid}`)}))})};