exports=async function(url,bid,cid,options){const{parseContentPaginate,hasMorePageCleaner,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#content",{clientOptions:{...(options&&options.clientOptions),followCookies:!0},hasNextFn:html=>null!=/\/\d+_\d+\.html/.exec(getLinkElement(html,"id=\"A3\"")),pageCleaner:hasMorePageCleaner,transformer:f=>f.replace(/<p>喜欢[^<]+请大家收藏：[^<]+<\/p>/,"")})};