exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},preprocess:html=>html.replace(/<div id="content">\s*[^<]+，最快更新<a href="[^"]+">[^<]+<\/a>最新章节！/,"<div id=\"content\">")})};