exports=async function(url,bid,cid,options){const{parseContent,trimBeforeContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions)},transformer:fragment=>fragment.replace(/(class="content_detail">\s*[^\n]+)\n\s*[^\s<]+\s*<\/p>/,(_,m)=>m+"</p>")})};