exports=async function(bid,options){const{parseCatalogPaginate,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`https://www.59xs.com/book/${bid}/${1===page?"":"index_"+page+".html"}`,"div#list>dl>dd",{...options,clientOptions,pageSize:60,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u9875"))?.subRegex(/\/index_(\d+)\.html/)?.intValue()})};