exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update"),url=`https://www.71qs.com/txt/${bid.split("|")[0]}.html`;return parseUpdate(url,html=>({name:"TXT",updateTime:html.subRegex(/id="uptime">\s*<strong>更新：<\/strong>\s*<span>([^<]+)</)}),{clientOptions:{...(options&&options.clientOptions),encoding:"gbk","x-mobile":0},format:"yyyy-M-d"})};