exports=async function(bid,options){const{parseCatalogPaginate,getSelectLastOptionSubRegex}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`https://www.800txt.info/book/${bid}/${1===page?"":"index_"+page+".html"}`,"div#list>dl>dd",{...options,clientOptions,pageSize:60,totalPagesRegex:h=>getSelectLastOptionSubRegex(h,/\/index_(\d+)\.html/)?.intValue()})};