exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(`https://www.aaread.club/ajax/chapter/chapterContent.php?_csrfToken=&bookId=${bid}&chapterId=${cid}&authorId=0&_=${new Date().getTime()}`,null,{httpOptions:{headers:{"X-Requested-With":"XMLHttpRequest",Referer:url}},clientOptions:{...(options&&options.clientOptions)},preprocess:h=>{h=JSON.parse(h).data.chapterInfo.content;const regex=/<style>\s*\.(\w+)\s*{\s*display:\s*none;\s*}\s*<\/style>/,s=h.subRegex(regex);return h.replace(regex,"").replace(new RegExp(`<[a-zA-Z\\d]+ [^>]*class=['"]${s}['"][^>]*>[^<]*<\\/[a-zA-Z\\d]+>`,"g"),"")}})};