exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment,buildImg}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/class="article-con"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},preprocess:html=>html.replace(/<div class="divimage">\s*<img src="([^"]+)"[^>]* class="imagecontent"[^>]*>\s*<\/div>/g,(_,m)=>`<br/>${buildImg(m)}<br/>`)})};