exports=async function(url,bid,cid,options){const{parseContentPaginate,trimContentFragment,getLinkElement}=await require("transcode/parse_content"),replaceFont=await require("transcode/font_map");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),{cleaner:html=>trimContentFragment(html,/id="chapter"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/[a-z\d]+_\d+\.html/.exec(getLinkElement(html,"id=\"linkNext\"")),preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4})<\/i>/g)})};