exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update"),replaceFont=await require("transcode/font_map");return parseUpdate(`https://www.aoqingshu.com/${bid}/`,"meta",{clientOptions:{...(options&&options.clientOptions),"x-mobile":0},preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4})<\/i>/g)})};