exports=async function(bid,options){const{parseInfo}=await require("transcode/parse_info"),replaceFont=await require("transcode/font_map");return parseInfo("\u50B2\u6674\u4E66\u57CE",`https://www.aozhiwenxue.com/${bid}/`,"meta",{clientOptions:{...(options&&options.clientOptions),"x-mobile":0},preprocess:html=>replaceFont(html,/<i>&#x([a-f\d]{4})<\/i>/g)})};