exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment,cleanContentAd,googleAdPattern}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/id="contents"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},preprocess:f=>f.replace(googleAdPattern,""),webview:!0}).then(cleanContentAd)};