exports=async function(url,bid,cid,options){const{parseContentPaginate,trimBeforeContent,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),{cleaner:html=>trimBeforeContent(html,/pt-read-text"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/read_\d+_\d+\.html/.exec(getLinkElement(html,"class=\"pt-nextchapter\""))})};