exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`https://m.baoshuu.com/TXT/down_${bid}.html`,html=>({name:"TXT",updateTime:html.subRegex(/<h3>简介：（入库时间(?:&nbsp;|\s)*(?<updateTime>[^<]+)）/)}),{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"}})};