exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`http://www.bibidushu.com/book/${bid}.html`,html=>({name:html.subRegexLast(/li>\s*<a href="[^"]*\/read\/\d+_\d+\.html"[^>]*>((?:[^<]|<\/?b>)+)</g).replace(/<\/?b>/g,"").trim(),updateTime:html.subRegex(/更新时间：(?<updateTime>[^<]+)<\/td/)}),{clientOptions:{...(options&&options.clientOptions),encoding:"gbk","x-mobile":0}})};