exports=async function(url,bid,cid,options){const{parseContentPaginate,trimContentFragment,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),{cleaner:html=>trimContentFragment(html,/id="nr1"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions),"x-mobile":1},hasNextFn:html=>/\/\d+_\d+\.html/.test(getLinkElement(html,"id=\"pt_next\""))})};