exports=async function(url,bid,cid,options){const{parseContentPaginate,brContentRegexFragment}=await require("transcode/parse_content"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),new RegExp(`id="nr1"[^>]*>${brContentRegexFragment}`),{...options,clientOptions,hasNextFn:html=>/id="pt_next"[^>]*href="\/book\/\d+\/\d+\/\d+_\d+\.html">下一页</.test(html)})};