exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/<div class="read-content[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},preprocess:html=>html.replace(/<p>\s*<p>/g,"<p>").replace(/<\/p>\s*<\/p>/g,"</p>")})};