exports=async function(bid,options){const{parseCatalogPaginate,cleanCatalogWrapper,getSelectLastOption,hrefRecordRegex}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`https://www.bqg13.com/book/${bid}/${1===page?"":"index_"+page+".html"}`,{cleaner:html=>cleanCatalogWrapper(html,/class="mulu_list"/,"</ul>"),recordRegex:hrefRecordRegex},{...options,clientOptions,pageSize:60,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u9875"))?.subRegex(/index_(\d+)\.html/)?.intValue()})};