exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update");return parseUpdate(`http://www.bwsk.com/${bid}/index.html`,html=>{const m=Array.from(html.matchAll(/<TD[^>]*>(?:\s*<center>)?\s*<A[^>]+>(?<name>[^<]+)<\//ig));return {name:0<m.length?m[m.length-1][1]:""}},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"}})};