exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},preprocess:raw=>raw.replace(/<h1>[^<]+<\/h1>|<div class="zboxf"><script>[^<]+<\/script><\/div>/g,"")})};