exports=async function(url,bid,cid,options){const{parseContentPaginate,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>/\/\d+\/\d+_\d+\.html/.test(getLinkElement(html,">\u4E0B\u4E00\u9875</a>"))})};