exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,"div#content",{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},transformer:f=>f.replace(/^(?:\[[^\]]*])?[^<>&\s]*\w+\.\w+\.\w{2,3}[^<>&]*/,"").replace(/(?:\d+小说网)?\w+\.\w+\.\w{2,3}[^<]+$/,"")})};