exports=async function(bid,options){const{parseUpdate}=await require("transcode/parse_update"),url=`http://www.cuoheng.com/html/${bid.idPrefix()}/${bid}/index.html`;return parseUpdate(url,null,{clientOptions:{...(options&&options.clientOptions),"x-mobile":0,encoding:"gbk"},parser:async(html,result)=>{const end=html.lastIndexOf("</dl>"),fragment=html.substring(html.lastIndexOf("<a",end),end);result.name=fragment.subRegex(/>([^<]+)<\//).trim();}})};