exports=async function(url,bid,cid,options){const{parseContentPaginate,getLinkElement}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".html",`_${page}.html`),"div#content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/\d+_\d+\.html/.exec(getLinkElement(html,">\u7FFB\u4E0B\u9875<"))})};