exports=async function(url,bid,cid,options){const{parseContent,trimBeforeContent}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimBeforeContent(html,/id="nr"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk"},transformer:f=>f.replace(/小提示：[^<]+退出阅读模式[^<]+/,"")})};