exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment,cleanContentAd,googleAdPattern}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/id="contents"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions)},webview:!0,preprocess:html=>html.replace(googleAdPattern,"")}).then(cleanContentAd)};