exports=async function(bid,options){const{parseCatalogPaginate}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions),encoding:"gbk"};return parseCatalogPaginate(page=>`https://www.d3shu.com/book/${bid}${1===page?"":"/index_"+page}.html`,".section-list",{...options,clientOptions,pageSize:20,totalPagesRegex:/value="(?:https?:\/\/www\.d3shu\.com)?\/book\/\d+\/index_(?<totalPages>\d+)\.html"[^>]*>[^<]+<\/option>\s*<\/select>/})};