exports=async function(bid,options){const{parseCatalogPaginate,cleanCatalogWrapper,hrefRecordRegex,getSelectLastOption}=await require("transcode/parse_catalog"),clientOptions={...(options&&options.clientOptions)};return parseCatalogPaginate(page=>`https://www.fyxfcw.com/book/${bid}/${1===page?"":"index_"+page+".html"}`,{cleaner:html=>cleanCatalogWrapper(html,/id="main"/,"</ul>"),recordRegex:hrefRecordRegex},{...options,clientOptions,pageSize:20,totalPagesRegex:html=>getSelectLastOption(html,s=>-1!==s.indexOf("\u7AE0"))?.subRegex(/\/index_(?<totalPages>\d+).html/)?.intValue()})};