exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content"),replaceFont=await require("transcode/pinyin_map");return parseContent(url,{cleaner:html=>trimContentFragment(html,/class="novelinfvie"[^>]*>/)},{clientOptions:{...(options&&options.clientOptions),encoding:"gbk","x-mobile":1},preprocess:html=>replaceFont(html,/<img src="[a-z]+\/([^"]+)\.jpg">/g)})};