exports=async function(url,bid,cid,options){const{parseContent}=await require("transcode/parse_content");return parseContent(url,"#chaptercontent",{clientOptions:{...(options&&options.clientOptions),"x-mobile":1},preprocess:h=>h.replace(/<\/?i>/g,""),transformer:f=>f.replace(/^\s*<p>\s*书包网[^<]+最新章节。\s*<\/p>/,"")})};