exports=async function(url,bid,cid,options){const{parseContentPaginate}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url.replace(".aspx",`_${page}.aspx`),"div#content",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/[a-z\d]+_\d+\.aspx"[^>]*>\s*下一章\s*<\/a>/.exec(html)})};