exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/pt-read-text[^>]+>/)},{clientOptions:{...(options&&options.clientOptions)},transformer:f=>f.replace(/ {4,}\n {4,}记住网址[^<]+/,"")})};