exports=async function(url,bid,cid,options){const{parseContent,trimContentFragment}=await require("transcode/parse_content");return parseContent(url,{cleaner:html=>trimContentFragment(html,/id="content"[^>]*>(?:\s*<div id='clickeye_content'>)?/)},{clientOptions:{...(options&&options.clientOptions)},transformer:f=>f.replace(/^(\s|&[#\w]+;)*\(?苦读书.+阅读。/,"").replace(/(\s|&[#\w]+;)*苦读书\s*\w+\.\w+\.\w+$/,"")})};