exports=async function(url,bid,cid,options){const{parseContentPaginate,getLinkElement,hasMorePageCleaner}=await require("transcode/parse_content");return parseContentPaginate(page=>1===page?url:url+"_"+page,"div#booktxt",{clientOptions:{...(options&&options.clientOptions)},hasNextFn:html=>null!=/\/\d+_\d+"/.exec(getLinkElement(html,"id=\"linkNext\"")),pageCleaner:hasMorePageCleaner})};